import React,{Component} from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
class Service extends Component {
    state = {
        services:[]
    }
    componentDidMount(){
        axios.get('data.json').then(res=>{this.setState({services:res.data.service})})
    }
    render() {
        const {services}=this.state;
        const serviceList=services.map(service=>{
            return (
               
                <div key={service.id} className="col-sm-6 col-md-4 ">
                        <div className="card">
                    <Link to={service.l}>
                            <img src={service.image} className="card-img-top" alt="..."/>
                    </Link>
                            <div className="card-body">
                            <p className="card-text">{service.body}</p>
                            </div>
                        </div>
                </div>
            )
        })
            return(
                <section className="services py-4">
                    <div className="container">
                        <div className="row text-center">
                            {serviceList}
                        </div>
                    </div>
                </section>
            )
    }
    
}
export default Service;
